b();

// 2
obj.b.apply({ a: 2 });

// 1
obj.c();

// undefined
obj.d();
obj.d();
obj.d.apply({ a: 2 });

// undefined
obj.e();
obj.e();
obj.e.call({ a: 2 });

// 1
obj.f()();
// 1
obj.f()();

// 1
obj.f().call({ a: 2 });
